var chai        = require('chai'),
    assert      = chai.assert,
    expect      = chai.expect,
    webdriverjs = require('../index'),
    client = webdriverjs.remote({
        desiredCapabilities: {
            browserName: 'chrome',
            version: '27',
            platform: 'XP',
            tags: ['examples'],
            name: 'This is an example test'
        },
        host: 'ondemand.saucelabs.com',
        port: 80,
        user: process.env.SAUCE_USERNAME,
        key: process.env.SAUCE_ACCESS_KEY,
        logLevel: 'silent'
    }).init();
 
client
    .url('http://m.olx.in/force/html4')
    .title(function(err, res) {
        console.log('Title was: ' + res.value);
    })
    .setValue('*[name="search"]','iphone')
    .click('*[type="submit"]')
    .getSource(function(err, res) {
        expect(res).to.contain('Search: iphone')
     }) 
    .end();